package com.mirkamol.food.ui.home

import android.os.Bundle
import android.view.View
import android.widget.Toast
import androidx.activity.addCallback
import androidx.fragment.app.viewModels
import androidx.navigation.fragment.findNavController
import by.kirich1409.viewbindingdelegate.viewBinding
import com.mirkamol.food.R
import com.mirkamol.food.data.local.entity.Category
import com.mirkamol.food.databinding.FragmentHomeBinding
import com.mirkamol.food.ui.BaseFragment
import com.mirkamol.food.ui.home.adapter.CategoryAdapter
import dagger.hilt.android.AndroidEntryPoint

@AndroidEntryPoint
class HomeFragment : BaseFragment(R.layout.fragment_home) {
    private val binding by viewBinding(FragmentHomeBinding::bind)
    private val adapter by lazy { CategoryAdapter() }
    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        initViews()
        setupUI()
        adapterClickManager()
    }

    private fun adapterClickManager() {
        adapter.onClick {
            findNavController().navigate(R.id.createFragment)
        }
    }

    private fun initViews() {
        binding.ivHistory.setOnClickListener {
            findNavController().navigate(R.id.historyFragment)
        }
    }

    private fun setupUI() {
        val list = ArrayList<Category>()
        list.add(Category("Oq tepa Lavash", "data:image/jpeg;base64,"))

        adapter.submitList(list)
        binding.recyclerView.adapter = adapter

    }
}